import React, { Component } from 'react'
import StudentService from './services/StudentService';

export default class UpdateStudentForm extends Component {

    constructor(props){
        super(props)

        this.state={
            idToGet: '',
            students:[]
        }

        this.changefirstName = this.changefirstName.bind(this);
        this.changelastName = this.changelastName.bind(this);
        this.changemail = this.changemail.bind(this);
    }

    UpdateStudent= (e) =>{
        e.preventDefault();



    }

    getnewStudent(){
        StudentService.getStudentById(this.state.idToGet).then((res)=>{

            this.setState({
                students: res.data
            });
        });
    }

    changeId= (e) =>{
        this.setState({idToGet: e.target.value});
    }

    changefirstName= (e) =>{
        this.setState({firstName: e.target.value});
    }

    changelastName= (e) =>{
        this.setState({lastName: e.target.value});
    }

    changemail= (e) =>{
        this.setState({email: e.target.value});
    }

    render() {
        return (
            <div>


                  <div>
						   <label htmlFor="contactName">Select Student by ID <span className="required">*</span></label>
						   <input value={this.state.idToGet} type="text"  size="35" id="contactName" name="contactName" onChange={this.changeId}/>
                  </div>

                  <div>
                  <button type='submit' onClick={this.getnewStudent} className="submit">Click to Search Student</button>
                  </div>

                  <h2>Selected Student By id</h2>
                <table>

                <thead>
                <tr>
                    <th>Id</th>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Email</th>
                </tr>
                </thead>


                <tbody>
                    {
                        this.state.students.map(
                            students=>
                            <tr key={students.id}>
                                <td>{students.id}</td>
                                <td>{students.firstName}</td>
                                <td>{students.lastName}</td>
                                <td>{students.email}</td>
                            </tr>
                        )
                    }
                </tbody>


            </table>

        </div>
        )
    }
}
