import React, { Component } from 'react'

export default class StudentForm extends Component {

    constructor(props){
        super(props)

        this.state={
            Id: '',
            firstName: '',
            lastName: '', 
            email: ''
        }
        this.changeId = this.changeId.bind(this);
        this.changefirstName = this.changefirstName.bind(this);
        this.changelastName = this.changelastName.bind(this);
        this.changemail = this.changemail.bind(this);
    }

    saveStudent= (e) =>{
        e.preventDefault();

        let newStudent = {
            ID: this.state.Id,
            FIRSTNAME: this.state.firstName,
            LASTNAME: this.state.lastName,
            EMAIL: this.state.email
        }

        console.log('students => ' + JSON.stringify(newStudent));
    }

    changeId= (e) =>{
        this.setState({Id: e.target.value});

    }

    changefirstName= (e) =>{
        this.setState({firstName: e.target.value});
    }

    changelastName= (e) =>{
        this.setState({lastName: e.target.value});
    }

    changemail= (e) =>{
        this.setState({email: e.target.value});
    }

    render() {
        return (
            <div>

            <form id="contactForm" name="contactForm">
					<fieldset>

                  <div>
						   <label htmlFor="contactName">ID <span className="required">*</span></label>
						   <input value={this.state.Id} type="text" defaultValue="" size="10" id="contactId" name="contactId" onChange={this.changeId}/>
                  </div>      

                  <div>
						   <label htmlFor="contactName">First Name <span className="required">*</span></label>
						   <input value={this.state.firstName} type="text" defaultValue="" size="35" id="contactName" name="contactName" onChange={this.changefirstName}/>
                  </div>

                  <div>
						   <label htmlFor="contactName">Last Name <span className="required">*</span></label>
						   <input value={this.state.lastName} type="text" defaultValue="" size="35" id="contactName" name="contactName" onChange={this.changelastName}/>
                  </div>

                  <div>
						   <label htmlFor="contactEmail">Email <span className="required">*</span></label>
						   <input value={this.state.email } type="text" defaultValue="" size="35" id="contactEmail" name="contactEmail" onChange={this.changemail}/>
                  </div>


                  <div>
                     <button type='submit' onClick={this.saveStudent} className="submit">Submit</button>
                     <span id="image-loader">
                        <img alt="" src='' />
                     </span>
                  </div>
					</fieldset>
				   </form>
        </div>
        )
    }
}
