import React, { useState } from 'react';
import StudentService from './services/StudentService';



function AddStudent() {
const [firstName, setFirstName] = useState('');
const [lastName, setLastName] = useState('');
const [email, setEmail] = useState('');

   const saveStudent= (e) =>{
        e.preventDefault();

        let newStudent = {

            firstName: this.state.firstName,
            lastName: this.state.lastName,
            email: this.state.email
        }

        StudentService.postStudent(newStudent);

        console.log('students => ' + JSON.stringify(newStudent));
    }

    return (

        <section id="contact">

        <div className="row section-head">

           <div className="two columns header-col">

              <h1><span>Get In Touch.</span></h1>

           </div>

           <div className="ten columns">


           </div>

        </div>
        <div className="row">
        <div className="eight columns">

           <form id="contactForm" name="contactForm">
                <fieldset>

              <div>
                       <label htmlFor="contactName">First Name <span className="required">*</span></label>
                       <input value={firstName} type="text" defaultValue="" size="35" id="contactName" name="contactName" onChange={e => setFirstName(e.target.value)}/>
              </div>

              <div>
                       <label htmlFor="contactEmail">Last Name <span className="required">*</span></label>
                       <input value={lastName} type="text" defaultValue="" size="35" id="contactEmail" name="contactEmail" onChange={e=> setLastName(e.target.value)}/>
              </div>

              <div>
                       <label htmlFor="contactSubject">Email</label>
                       <input value={email} type="text" defaultValue="" size="35" id="contactSubject" name="contactSubject" onChange={e => setEmail(e.target.value)}/>
              </div>


              <div>
                 <button type='submit' onClick={saveStudent} className="submit">Submit</button>
                 <span id="image-loader">
                    <img alt="" src="images/loader.gif" />
                 </span>
              </div>
                </fieldset>
               </form>

       </div>

  </div>
  </section>
    )
}

export default AddStudent;
